import React, { useEffect, useState } from "react";
import styles from "../pages/Project.module.css";
import { useParams } from "react-router-dom";
import Loading from '../layout/Loading'
import Container from '../layout/Container'
import ProjectForm from '../project/ProjectForm'

function Project() {
  const [project, setProject] = useState([]);
  const [showProjectForm, setShowProjectForm] = useState(false)
  const [message, setMessage] = useState()
  const [type, setType] = useState()

  const { id } = useParams();

  useEffect(() => {
    setTimeout(()=>{
        fetch(`http://localhost:5000/projects/${id}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((resp) => resp.json())
      .then((data) => {
        setProject(data);
      })
      .catch((err) => console.log(err));
    }, 500)
  }, [id]);

function editPost(project){
  //budget validation
  if(project.budget < project.cost){
    // massage
  }
  fetch(`http://localhost:5000/projects/${id}`, {
    method: 'PATCH',
    headers: {
      'Content-Type': 'application/json',
    },
    body: JSON.stringify(project),
  })
  .then(resp => resp.json())
  .then((data)=>{
    setProject(data)
    setShowProjectForm(false)
    //message
  })
  .catch((err)=> console.log(err))
}

function toggleProjectForm(){
    setShowProjectForm(!showProjectForm)
}

  return (
  <>
  {project.name ? (
    <div className={styles.project_details}>
        <Container className='column'>
            <div className={styles.details_container}>
                <h1>Projeto: {project.name}</h1>
                <button className={styles.btn} onClick={toggleProjectForm}>{!showProjectForm ?
                'Editar Projeto' : 'Fechar'}</button>
                {!showProjectForm ? (
                    <div className={styles.project_info}>
                        <p>
                            <span>Categoria</span> {project.category.name}
                        </p>
                        <p>
                            <span>Total de orçamento</span> R${project.budget}
                        </p>
                        <p>
                            <span>Total utilizado</span> R${project.cost}
                        </p>
                    </div >
                    ) : (
                        <div className={styles.project_info}>
                            <ProjectForm handleSubmit={editPost} 
                            btnText="Concuir edição" projectData={project}/>
                        </div>
                    )}
            </div>
        </Container>
    </div>
    ) : ( 
        <Loading />)}
        </>
  )}
export default Project;
