import React, { useState } from 'react';
import styles from './Message.module.css'
function Message({type, msg}){

    const [visible, setVisible] = useState(false)

    return(

        <>
         {visible && (
             <div className={`${styles.messages} ${styles[type]}`}>
             {msg}
         </div>
         )}
        </>
        
    )
}
export default Message;
